import React, {createContext} from 'react'
import { Button } from './Btn'



export const Player1 = () => {

    function play(){
        let play11=Math.floor(Math.random()*7)+ Math.floor(Math.random()*7)
        console.log(play11)
        document.getElementById('rollinfo1').innerText=play11
    }

    const player1 = createContext()

    return (
      <player1.Provider play1={play}>
        <div id="Player1">
          <p>
            Player Rolled:<span id="rollinfo1"></span>
          </p>
          <button onClick={play}> Play1 </button>
        </div>
      </player1.Provider>
    );


}
