import React from 'react'



export const Player2 = () => {
    let play22=Math.floor(Math.random()*7)+ Math.floor(Math.random()*7)
    function play(){
        let play22=Math.floor(Math.random()*7)+ Math.floor(Math.random()*7)
        console.log(play22)
        document.getElementById('rollinfo2').innerText=play22
    }

    function rste (){
        document.getElementById('rollinfo2').innerText=''
    }

    return (
        <div id ='Player2'>
            <p>Player2 Rolled:<span id='rollinfo2'></span></p>
            <button onClick= {play}>Play2</button>
            <button onClick= {rste}>Reset</button>

        </div>
    )


}
